props.className:
`w-2.5 h-2.5 rounded-full transition-all ${
                index === currentSlide
                  ? 'bg-primary w-8'
                  : 'bg-gray-300 dark:bg-gray-600 hover:bg-gray-400 dark:hover:bg-gray-500'
              }`

`flex-shrink-0 w-20 h-20 rounded-lg overflow-hidden border-2 transition-all focus:outline-none focus:ring-2 focus:ring-accent dark:focus:ring-primary-light ${
                    selectedIndex === index
                      ? 'border-accent dark:border-primary-light ring-2 ring-accent/20 dark:ring-primary-light/20'
                      : 'border-gray-200 dark:border-gray-700 hover:border-gray-300 dark:hover:border-gray-600'
                  }`

`${isMobile ? 'w-2 h-2' : 'w-3 h-3'} rounded-full transition-all duration-300 ${
                  index === currentStep
                    ? 'bg-white scale-125 shadow-lg shadow-white/50'
                    : 'bg-white/40 hover:bg-white/60 hover:scale-110'
                } ${isAnimating ? 'cursor-not-allowed' : 'cursor-pointer'}`

`w-full p-5 hover:bg-gradient-to-r hover:from-primary/5 hover:via-primary/10 hover:to-primary/5 dark:hover:from-primary-light/10 dark:hover:via-primary-light/15 dark:hover:to-primary-light/10 transition-all duration-300 text-left flex items-center gap-5 border-b border-gray-100 dark:border-gray-700/50 last:border-b-0 group relative overflow-hidden ${
                  summary.unreadCount > 0 ? 'bg-primary/5 dark:bg-primary-light/5' : ''
                }`

props.onClick:
() => {
                    setError(null);
                    setShowNewSessionOption(false);
                    setLoadingPhase('idle'); // Reset loading phase for retry
                    if (!tradeAnchor) {
                      loadUserItems();
                    } else if (session && user) {
                      loadItemPool();
                    } else {
                      // If no session, go back to anchor selection
                      setIsSwipeMode(false);
                      setTradeAnchor(null);
                    }
                  }

() => {
                  // Set the offer to decline before closing compare modal
                  const offer = offers.find(o => o.id === selectedOffer.id);
                  if (offer) {
                    setOfferToDecline(offer);
                    setDeclineModalOpen(true);
                  }
                  setCompareModalOpen(false);
                }
